
public class ejercicio_uno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 144;
		int y = 999;
		System.out.println("----------------------------");
		System.out.println("Valores de x y de y");
		System.out.println("x:" + x);
		System.out.println("y:" + y);
		System.out.println("----------------------------");
		System.out.println("Suma de x + y:");
		System.out.println(x+y);
		System.out.println("Resta de x - y :");
		System.out.println(x-y);
		System.out.println("Multiplicación de x * y:");
		System.out.println(x*y);
		System.out.println("División de x / y :");
		System.out.println(x/y);
		System.out.println("----------------------------");
		System.out.println("Suma de y + x:");
		System.out.println(y+x);
		System.out.println("Resta de y - x :");
		System.out.println(y-x);
		System.out.println("Multiplicación de y * x:");
		System.out.println(y*x);
		System.out.println("División de y / x :");
		System.out.println(y/x);
	}

}
